/*
    Ping Pong pong.
    Instructions: run nodejs ping.js
                  run nodejs pong.js
                  You may run these in any order.
*/

'use strict';

const dds = require('vortexdds');
const path = require('path');

main();

function main(){
  pong().then(() => {
    console.log('=== Pong end');
    process.exit(0);
  }).catch((error) => {
    console.log('Error: ' + error.message);
    process.exit(1);
  });
}

async function pong() {

  console.log('=== Pong start');

  let participant = null;
  try {
    // create our entities
    participant = new dds.Participant();

    const topicName = 'PingPongData_Msg';
    const idlName = 'PingPongTopic.idl';
    const idlPath = __dirname + path.sep + idlName;

    const typeSupports = await dds.importIDL(idlPath);
    const typeSupport = typeSupports.get('PingPongData::Msg');

    const topic = participant.createTopic(
      topicName,
      typeSupport
    );

    // create a subscriber on partition Ping
    // on ping, we have a publisher with the same partition
    const sqos = dds.QoS.subscriberDefault();

    sqos.partition = {names: 'Ping'};
    const subscriber = participant.createSubscriber(sqos, null);

    const reader = subscriber.createReader(topic);

    // create a publisher on partition Pong
    // on ping, we have a subscriber on the same partition
    const pqos = dds.QoS.publisherDefault();

    pqos.partition = {names: 'Pong'};
    const publisher = participant.createPublisher(pqos);

    const wqos = new dds.QoS({
      reliability: {kind: dds.ReliabilityKind.BestEffort}});
    const writer = publisher.createWriter(topic, wqos);

    // create waitset which waits until ping writer from ping.js is found
    let subMatchedCond = null;
    let subMatchedWaitset = null;
    try {
      subMatchedCond = reader
        .createStatusCondition(dds.StatusMask.subscription_matched);
      subMatchedWaitset = new dds.Waitset(subMatchedCond);

      // wait until ping has been found
      console.log('Waiting for ping publisher to be found...');
      await subMatchedWaitset.wait(dds.DDSConstants.DDS_INFINITY);
      console.log('Found ping publisher.');
    } finally {
      if (subMatchedWaitset !== null){
        subMatchedWaitset.delete();
      }
    }

    // waitset for new data
    // wait for data, and resend in a ping/pong
    // loop for 20 iterations
    let newDataCondition = null;
    let newDataWaitset = null;
    try {
      newDataCondition = reader.createReadCondition(
        dds.StateMask.sample.not_read
      );
      newDataWaitset = new dds.Waitset(newDataCondition);

      /* Loop 20 times: when we have new data, it
        reads it, prints it, and sends back a message with the userID
        field incremented by one and 'from pong' in the message field.*/

      for (let i = 0; i < 20; i++) {
        // wait for new data
        await newDataWaitset.wait();

        // new data incoming
        let takeArray = reader.take(1);
        if (takeArray.length > 0 && takeArray[0].info.valid_data) {
          let sample = takeArray[0].sample;
          console.log('received: ' + JSON.stringify(sample));
          let msg = {userID: sample.userID, message: 'from pong'};
          console.log('replying with: ' + JSON.stringify(msg));
          writer.write(msg);
        }
      }

    } finally {
      if (newDataWaitset !== null){
        newDataWaitset.delete();
      }
    }

  } finally {
    console.log('=== Cleanup resources');
    if (participant !== null){
      participant.delete().catch((error) => {
        console.log('Error cleaning up resources: '
          + error.message);
      });
    }
  }

}
